sqlMap.select:
WITH T1 AS ( SELECT CARRY,IV_NO,PO_NO,PO_LINE_NO,ITEM_CD,SUM(CAST(REPLACE(QTY,',','') AS float)) QTY FROM IMS_RPA_IMPORT_NOTIFY WHERE DEL_YN IS NULL GROUP BY CARRY,IV_NO,PO_NO,PO_LINE_NO,ITEM_CD
), T3 AS (
SELECT DISTINCT * FROM IMS_RPA_FORWARDER WHERE DEL_YN IS NULL AND CONVERT(INT,REPLACE(RTRIM(LTRIM(BL_SEONJEOG_DT)),'.','')) > '20190831' ), T4 AS (
SELECT DISTINCT *,REPLACE(d270,' ','') d270_KEY FROM IMS_RPA_CUSTOMS_AGENT WHERE DEL_YN IS NULL AND isdate(d32) = 1 AND isdate(d125) = 1
) SELECT DISTINCT H_BL_NO t3HBlNo ,FORMAT(CONVERT(date, d32),'yyyy.MM.dd') t4TgYcDt ,IIF(d3 LIKE '%신대동%', '60747', IIF(d3 LIKE '%더블유%', '43199', d3)) t4SinGoJa ,d1 t4SingoNo ,d27 t4SGSG ,FORMAT(CONVERT(date, d125),'yyyy.MM.dd') t4SingoDt ,RTRIM(LTRIM(STR(REPLACE(d106,',',''), 50, 2))) t4CifUsd ,RTRIM(LTRIM(STR(REPLACE(d105,',',''), 50, 0))) t4CifKrw ,RTRIM(LTRIM(STR(REPLACE(d115,',',''), 50, 0))) t4BGSGP ,RTRIM(LTRIM(STR(REPLACE(d107,',',''), 50, 0))) t4GwanSe ,RTRIM(LTRIM(STR(REPLACE(d117,',',''), 50, 0))) t4Bugase ,PO_NO t1PoNo ,PO_LINE_NO t1PoLineNo ,d267 t4LanNo2 ,d268 t4HeangNo ,d196 t4GSY ,RTRIM(LTRIM(STR(REPLACE(d201,',',''), 50, 0))) t4SJGSE FROM T3 ,T1 ,T4 WHERE T3.H_BL_NO = T4.d52 AND T3.IN_NO = T1.IV_NO AND T1.QTY = T4.d276 AND T1.ITEM_CD = t4.d270_KEY AND T3.H_BL_NO = #searchBlNo# ORDER BY t4LanNo2,t4HeangNo

WITH T1 AS ( SELECT ROW_NUMBER() OVER(PARTITION BY CARRY,IV_NO,ITEM_CD,sum(cast(replace (QTY, ',', '') as float)) ORDER BY PO_NO ASC) as rnum, CARRY,IV_NO,PO_NO,PO_LINE_NO,ITEM_CD,SUM(CAST(REPLACE(QTY,',','') AS float)) QTY FROM IMS_RPA_IMPORT_NOTIFY WHERE DEL_YN IS NULL GROUP BY CARRY,IV_NO,PO_NO,PO_LINE_NO,ITEM_CD
), T2 AS ( SELECT DISTINCT VAL IV_NO, ACNT_DT FROM ( SELECT DISTINCT IV_NO_1,IV_NO_2,IV_NO_3,IV_NO_4,IV_NO_5,IV_NO_6,IV_NO_7,IV_NO_8,IV_NO_9, ACNT_DT FROM [MOT_RPQ].[dbo].[IMS_RPA_CARGO_INSURANCE] WHERE DEL_YN is null ) as ST UNPIVOT ( VAL FOR COL IN ( IV_NO_1,IV_NO_2,IV_NO_3,IV_NO_4,IV_NO_5,IV_NO_6,IV_NO_7,IV_NO_8,IV_NO_9 ) ) as PT WHERE LEN(VAL) > 0 ), T3 AS (
SELECT DISTINCT * FROM IMS_RPA_FORWARDER WHERE DEL_YN IS NULL AND CONVERT(INT,REPLACE(RTRIM(LTRIM(BL_SEONJEOG_DT)),'.','')) > '20190831' AND H_BL_NO = #searchBlNo# ), T4 AS (
SELECT DISTINCT *,REPLACE(d270,' ','') d270_KEY ,convert(nvarchar,d267)+convert(nvarchar,d268) as seq_key FROM IMS_RPA_CUSTOMS_AGENT WHERE DEL_YN IS NULL
) SELECT t1InNo, IIF(t1Carry = 'SEA', 'O', IIF(t1Carry = 'AIR', 'A', t1Carry)) t1Carry, REPLACE(t2AcntDt, '-', '.') t3SSDt, t1PoNo, t1PoLineNo, t1ItemCd, t1Qty, t4DanGa, t4DanGa2, t4GeumAeg, t4JeCurDw, t4SebeonCd, t3GgCoCd2, t3HBlNo, t4IndoJk FROM( select ROW_NUMBER() OVER(PARTITION BY T1.IV_NO,T1.PO_NO, T1.PO_LINE_NO,T4.d270_KEY ORDER BY T4.seq_key ASC) as rnum, T4.seq_key, T1.rnum as t1rnum, T1.IV_NO t1InNo, T1.CARRY t1Carry, T3.SEOLYU_SONGBU_DT t3SSDt, T1.PO_NO t1PoNo, T1.PO_LINE_NO t1PoLineNo, T1.ITEM_CD t1ItemCd, T1.QTY t1Qty, case when CHARINDEX('.', d278) = 0 then d278 else case when len(substring(d278, CHARINDEX('.', d278) + 1, len(d278))) = 1 then round(d278, 1) * 10 when len(substring(d278, CHARINDEX('.', d278) + 1, len(d278))) = 2 then round(d278, 2) * 100 end end t4DanGa, case when CHARINDEX('.', d278) = 0 then 1 else case when len(substring(d278, CHARINDEX('.', d278) + 1, len(d278))) = 0 then 1 when len(substring(d278, CHARINDEX('.', d278) + 1, len(d278))) = 1 then 10 when len(substring(d278, CHARINDEX('.', d278) + 1, len(d278))) = 2 then 100 end end t4DanGa2, cast(replace(T4.d279,'null',0) as float) t4GeumAeg, T4.d68 t4JeCurDw, T4.d162 t4SebeonCd, T3.GONGGEUB_CO_CD_2 t3GgCoCd2, T3.H_BL_NO t3HBlNo, T4.d67 t4IndoJk, T2.ACNT_DT t2AcntDt
from T4 T4 left outer join T3 T3 on T4.d52 = T3.H_BL_NO left outer join T2 T2 on T3.IN_NO = T2.IV_NO left outer join T1 T1 on T1.IV_NO = T3.IN_NO and T1.QTY = T4.d276 and T1.ITEM_CD = T4.d270_KEY and T1.QTY = T4.d276 where T3.H_BL_NO = #searchBlNo# and T1.IV_NO is not NULL ) TOT where (rnum = 1 and t1rnum = 1) or (rnum = 2 and t1rnum = 2) order by seq_key

sqlMap.insert:
WITH T1 AS ( SELECT distinct BKTXT, ZFIMDNO, ZFACDO, BELNR, COND_TYPE, VTEXT, SPRAS, WRBTR, DMBTR, KURSF, WAERS, BLDAT, BUDAT, LIFNR, ZFCSTGRP, MWSKZ, WMWST, ZFCD, ZTERM, NAME1, STCD2 FROM( select * from OPENQUERY(ERP_DL, ' select A.BKTXT, A.ZFIMDNO, A.ZFACDO, D.BELNR, B.COND_TYPE, C.VTEXT, C.SPRAS, case when A.WAERS in (''KRW'',''JPY'') then A.WRBTR * 100 else A.WRBTR end WRBTR, case when A.WAERS in (''KRW'',''JPY'',''CNY'',''USD'') then A.DMBTR * 100 else A.DMBTR end DMBTR, case when A.KURSF = 0 then 1.00 else A.KURSF end KURSF, A.WAERS, A.BLDAT, A.BUDAT, A.LIFNR, A.ZFCSTGRP, A.MWSKZ, case when A.WAERS in (''KRW'',''JPY'') then A.WMWST * 100 else A.WMWST end WMWST, B.ZFCD, '''' ZTERM, E.NAME1, E.STCD2 from [wjp].ZIMTBKPF A inner join [wjp].ZIMTBSEG B on A.BELNR = B.BELNR and A.GJAHR = B.GJAHR and A.ZFPOSYN = ''Y'' left outer join [wjp].[BKPF] D on A.MANDT = D.MANDT and A.BUKRS = D.BUKRS and A.ZFACDO = D.BELNR and A.GJAHR = D.GJAHR and A.ZFACDO + A.GJAHR = D.AWKEY and left(D.BELNR,1) = ''5'' left outer join [wjp].T685T C on B.COND_TYPE = C.KSCHL and C.SPRAS = ''3'' left outer join [wjp].[LFA1] E on A.LIFNR = E.LIFNR
A.BKTXT = ''$searchBlNo$''
') union all select * from OPENQUERY(ERP_DL, ' select A.ZFCIVNO as BKTXT, A.ZFCIVRN as ZFIMDNO, B.BELNR as ZFACDO, D.BELNR, ''FR00'' as COND_TYPE, N''물대'' as VTEXT, 0 SPRAS, case when A.ZFIVAMC in (''KRW'',''JPY'') then A.ZFIVAMP * 100 else A.ZFIVAMP end WRBTR, case when A.ZFIVAMC in (''KRW'',''JPY'',''CNY'',''USD'') then A.ZFIVAMK * 100 else A.ZFIVAMK end DMBTR, case when A.ZFEXRT = 0 then 1.00 else A.ZFEXRT end KURSF, A.ZFIVAMC as WAERS, 0 BLDAT, A.ZFBUDAT as BUDAT, A.ZFMAVN as LIFNR, 0 ZFCSTGRP, '''' as MWSKZ, 0 as WMWST, '''' as ZFCD, A.ZTERM, E.NAME1, E.STCD2 from [wjp].ZIMTCIVHD A left outer join [wjp].ZIMTCIVHST B on A.ZFCIVRN = B.ZFCIVRN left outer join [wjp].[BKPF] D on A.MANDT = D.MANDT and A.BUKRS = D.BUKRS and B.BELNR = D.BELNR and B.GJAHR = D.GJAHR and B.BELNR + B.GJAHR = D.AWKEY left outer join [wjp].[LFA1] E on A.ZFMAVN = E.LIFNR
A.ZFCIVNO = ''$searchBlNo$''
') union all SELECT [BKTXT] COLLATE Korean_Wansung_CI_AS as [BKTXT] ,[ZFIMDNO] COLLATE Korean_Wansung_CI_AS as [ZFIMDNO] ,[ZFACDO] COLLATE Korean_Wansung_CI_AS as [ZFACDO] ,[BELNR] COLLATE Korean_Wansung_CI_AS as [BELNR] ,[COND_TYPE] COLLATE Korean_Wansung_CI_AS as [COND_TYPE] ,[VTEXT] COLLATE Korean_Wansung_CI_AS as [VTEXT] ,[SPRAS] COLLATE Korean_Wansung_CI_AS as [SPRAS] ,[WRBTR] ,[DMBTR] ,[KURSF] ,[WAERS] COLLATE Korean_Wansung_CI_AS as [WAERS] ,[BLDAT] COLLATE Korean_Wansung_CI_AS as [BLDAT] ,[BUDAT] COLLATE Korean_Wansung_CI_AS as [BUDAT] ,[LIFNR] COLLATE Korean_Wansung_CI_AS as [LIFNR] ,[ZFCSTGRP] COLLATE Korean_Wansung_CI_AS as [ZFCSTGRP] ,[MWSKZ] COLLATE Korean_Wansung_CI_AS as [MWSKZ] ,[WMWST] ,[ZFCD] COLLATE Korean_Wansung_CI_AS as [ZFCD] ,[ZTERM] COLLATE Korean_Wansung_CI_AS as [ZTERM] ,[NAME1] COLLATE Korean_Wansung_CI_AS as [NAME1] ,[STCD2] COLLATE Korean_Wansung_CI_AS as [STCD2] FROM [dbo].[RPA_SAP_TB]
BKTXT = #searchBlNo#
) tot
#nameVOList[]#
)
MERGE RPA_SAP_TB tgt USING ( SELECT * FROM T1 ) AS src ON tgt.BKTXT COLLATE Korean_Wansung_CI_AS = src.BKTXT AND tgt.BELNR COLLATE Korean_Wansung_CI_AS = src.BELNR AND tgt.COND_TYPE COLLATE Korean_Wansung_CI_AS = src.COND_TYPE WHEN MATCHED THEN UPDATE SET COND_TYPE = src.COND_TYPE , VTEXT = src.VTEXT , SPRAS = src.SPRAS , WRBTR = src.WRBTR , DMBTR = src.DMBTR , KURSF = src.KURSF , WAERS = src.WAERS , BLDAT = src.BLDAT , BUDAT = src.BUDAT , LIFNR = src.LIFNR , ZFCSTGRP = src.ZFCSTGRP , MWSKZ = src.MWSKZ , WMWST = src.WMWST , ZFCD = src.ZFCD , ZTERM = src.ZTERM , NAME1 = src.NAME1 , STCD2 = src.STCD2 , DEL_FLAG = 'N' , MOD_DT = getdate() WHEN NOT MATCHED THEN INSERT (BKTXT,ZFIMDNO,ZFACDO,BELNR,COND_TYPE,VTEXT,SPRAS,WRBTR,DMBTR,KURSF,WAERS, BLDAT,BUDAT,LIFNR,ZFCSTGRP,MWSKZ,WMWST,ZFCD,ZTERM,NAME1,STCD2,DEL_FLAG,MOD_DT) VALUES (src.BKTXT,src.ZFIMDNO,src.ZFACDO,src.BELNR,src.COND_TYPE,src.VTEXT,src.SPRAS,src.WRBTR,src.DMBTR,src.KURSF,src.WAERS, src.BLDAT,src.BUDAT,src.LIFNR,src.ZFCSTGRP,src.MWSKZ,src.WMWST,src.ZFCD,src.ZTERM,src.NAME1,src.STCD2,'N',getdate());

MERGE INTO RPA_STATEMENT_TB AS tar USING (
( case when ISNULL(#List[].ZFIMDNO#,'') = '' then #List[].ZFCARNM# else ISNULL(#List[].ZFBLNO#,'') end ,#yyyymm# ,case when ISNULL(#List[].ZFIMDNO#,'') = '' then 'MON' else 'BL' end ,ISNULL(#List[].NUM#,0) ,ISNULL(#List[].ZFACDO#,'') ,ISNULL(#List[].ZFIMDNO#,'') ,ISNULL(#List[].ZFIDRNO#,'') ,ISNULL(#List[].ZFINRC#,'') ,ISNULL(#List[].NAME1#,'') ,ISNULL(#List[].LIFNR#,'') ,ISNULL(#List[].STCD2#,'') ,ISNULL(#List[].ZFBLNO#,'') ,ISNULL(#List[].ZFAPNM#,'') ,case when LTRIM(#List[].TWRBTR#) = '' then 0 else IIF(#List[].TWRBTR# is null , NULL, CONVERT(decimal(20,0), #List[].TWRBTR#)) end ,case when LTRIM(#List[].WRBTR_TAX#) = '' then 0 else IIF(#List[].WRBTR_TAX# is null , NULL, CONVERT(decimal(20,0), #List[].WRBTR_TAX#)) end ,ISNULL(#List[].WAERS1#,'') ,case when LTRIM(#List[].SWRBTR#) = '' then 0 else IIF(#List[].SWRBTR# is null , NULL, CONVERT(decimal(20,0), #List[].SWRBTR#)) end ,ISNULL(#List[].WAERS2#,'') ,ISNULL(#List[].BUDAT#,'') ,ISNULL(#List[].ZFCSTGRP#,'') ,case when ISNULL(#List[].ZFIMDNO#,'') = '' then '' else ISNULL(#List[].ZFCARNM#,'') end ,ISNULL(#List[].BLDAT#,'') )
) AS src ( [STATEMENT_KEY] ,[YYYYMM] ,[GUBUN] ,[NUM] ,[ZFACDO] ,[ZFIMDNO] ,[ZFIDRNO] ,[ZFINRC] ,[NAME1] ,[LIFNR] ,[STCD2] ,[ZFBLNO] ,[ZFAPNM] ,[TWRBTR] ,[WRBTR_TAX] ,[WAERS1] ,[SWRBTR] ,[WAERS2] ,[BUDAT] ,[ZFCSTGRP] ,[ZFCARNM] ,[BLDAT] ) ON tar.STATEMENT_KEY = src.STATEMENT_KEY WHEN MATCHED THEN UPDATE SET NUM=src.NUM ,ZFACDO=src.ZFACDO ,ZFIMDNO=src.ZFIMDNO ,ZFIDRNO=src.ZFIDRNO ,ZFINRC=src.ZFINRC ,NAME1=src.NAME1 ,LIFNR=src.LIFNR ,STCD2=src.STCD2 ,ZFBLNO=src.ZFBLNO ,ZFAPNM=src.ZFAPNM ,TWRBTR=src.TWRBTR ,WRBTR_TAX=src.WRBTR_TAX ,WAERS1=src.WAERS1 ,SWRBTR=src.SWRBTR ,WAERS2=src.WAERS2 ,BUDAT=src.BUDAT ,ZFCSTGRP=src.ZFCSTGRP ,ZFCARNM=src.ZFCARNM ,BLDAT=src.BLDAT ,REG_DT=getdate() WHEN NOT MATCHED BY TARGET THEN INSERT ( [STATEMENT_KEY] ,[YYYYMM] ,[GUBUN] ,[NUM] ,[ZFACDO] ,[ZFIMDNO] ,[ZFIDRNO] ,[ZFINRC] ,[NAME1] ,[LIFNR] ,[STCD2] ,[ZFBLNO] ,[ZFAPNM] ,[TWRBTR] ,[WRBTR_TAX] ,[WAERS1] ,[SWRBTR] ,[WAERS2] ,[BUDAT] ,[ZFCSTGRP] ,[ZFCARNM] ,[BLDAT] ) VALUES ( src.[STATEMENT_KEY] ,src.[YYYYMM] ,src.[GUBUN] ,src.[NUM] ,src.[ZFACDO] ,src.[ZFIMDNO] ,src.[ZFIDRNO] ,src.[ZFINRC] ,src.[NAME1] ,src.[LIFNR] ,src.[STCD2] ,src.[ZFBLNO] ,src.[ZFAPNM] ,src.[TWRBTR] ,src.[WRBTR_TAX] ,src.[WAERS1] ,src.[SWRBTR] ,src.[WAERS2] ,src.[BUDAT] ,src.[ZFCSTGRP] ,src.[ZFCARNM] ,src.[BLDAT] );
